set tSC = ##class(%RoutineMgr).GetWebServerPort(.tPort) if $$$ISERR(tSC) set tSC = $$$ERROR($$$GeneralError, "Could not get web server port") quit set tHost = $zconvert($$$HSNetworkHostName, "L") set tLocalHostAndPort = tHost_$select(tPort'="":":",1:"")_tPort set FHIRVersion="DSTU2" set tBundleType = "transaction" set tMessageContentType = ..FHIRFormat set tMethodContentType = $select(..FHIRFormat["json":"json",1:"xml") set tFHIRRequest = ##class(HS.Message.FHIR.Request).%New() set tFHIRRequest.Interaction = Interaction set tFHIRRequest.Type = "Bundle" set tFHIRRequest.ContentType = tMessageContentType set tFHIRRequest.FHIRVersion = FHIRVersion set tFHIRRequest.FormatFHIROutput = ..FormatFHIROutput set tFHIRRequest.SessionApplication =..SessionApplication set tFHIRRequest.SessionId = "" set tFHIRRequest.Id = pMRN // Set the HOST header to provide for the case where the transaction is // being routed within this production (e.g., to HS.FHIR.Server.Process). do tFHIRRequest.Headers.SetAt(tLocalHostAndPort,"HOST") do tFHIRRequest.Payload.CopyFrom(pBundleStream) // *** Optional full transaction response //do tFHIRRequest.Headers.SetAt("return=representation","PREFER")
#; Set the HOST header to provide for the case where the transaction is #; being routed within this production (e.g., to HS.FHIR.Server.Process). do tFHIRRequest.Headers.SetAt(tLocalHostAndPort,"HOST") set tFHIRRequest.FormatFHIROutput = ..FormatFHIROutput set tFHIRRequest.SessionApplication = ..SessionApplication set tFHIRRequest.SessionId = "" do tFHIRRequest.AdditionalInfo.SetAt(1,"ConvertUUIDs") // Add Patient data to FHIR repository set tSC = ..SendRequestSync(..FHIRRepoConfigName, tFHIRRequest, .tFHIRResponse) if $$$ISERR(tSC) quit if (..OutputToQuickStream)&&($isobject($get(tQuickStream))) do tQuickStream.Clear() if ($isobject($get(tFHIRResponse)))&&(tFHIRResponse.%Extends("HS.Message.FHIR.Base"))&&(tFHIRResponse.QuickStreamId'="") { set tResponseQuickStream = ##class(HS.SDA3.QuickStream).%OpenId(tFHIRResponse.QuickStreamId) if $isobject(tResponseQuickStream) do tResponseQuickStream.Clear() set tFHIRResponse.QuickStreamId = "" }
