>UnresolvedBundlePlaceholder" #define HSFHIRErrUnsupportedModifier "<HSFHIRErr>UnsupportedModifier" #define HSFHIRErrUnsupportedParameter "<HSFHIRErr>UnsupportedParameter" #define HSFHIRErrUnsupportedParameterModifier "<HSFHIRErr>UnsupportedParameterModifier" #define HSFHIRErrUnsupportedSortKey "<HSFHIRErr>UnsupportedSortKey" #define HSFHIRErrUnsupportedTypeForEverything "<HSFHIRErr>UnsupportedTypeForEverything" #define HSFHIRErrValidationFailure "<HSFHIRErr>ValidationFailure" #define HSFHIRErrValueNotInEnumeration "<HSFHIRErr>ValueNotInEnumeration" #define HSFHIRErrWrongJsonType "<HSFHIRErr>WrongJsonType" #define HSFHIRErrWrongReadPostProcessorVersion "<HSFHIRErr>WrongReadPostProcessorVersion" #define HSFHIRErrWrongSearchPostProcessorVersion "<HSFHIRErr>WrongSearchPostProcessorVersion" #define HSFHIRErrWrongSessionHandlerVersion "<HSFHIRErr>WrongSessionHandlerVersion"

Method.Implementation:
set repid=$increment(^CacheTemp("TRAK")) set ind=1, END = 0, END1 = 0
if endpoint'="" {
set namespace=##class(TCITK.Utils).GetEndpointNamespace(endpoint)
set currentNS = $Namespace set $Namespace = namespace set sc=##class(Ens.Director).CreateBusinessService("TCAPI.Service",.tAPIService) set tRequest=##class(TrakCare.WebServices.SYSAPI.Production.Request.PAPersonListExecuteQueryRequest).%New() set tRequest.params.MAXCOUNT=maxcount set tRequest.params.hiddenFlag="On^^^^^^^^On" // inpatient only #Dim tResponse as TrakCare.WebServices.SYSAPI.Production.Response.PAPersonListExecuteQueryResponse set sc=tAPIService.SendAPI(tRequest,.tResponse)
if traceid'="" { set tRequestID=tRequest.%Id() set tRequestClass=tRequest.%ClassName() set SessionId="" &sql(Select %nolock ID Into :SessionId From Ens.MessageHeader where MessageBodyId = :tRequestID and SourceConfigName='TCAPI.Service') set tURL="/csp/healthshare/"_namespace_"/EnsPortal.VisualTrace.zen?SESSIONID="_SessionId
set $Namespace = currentNS
set ^zTrace(traceid,"sessionurl")= tURL set ^zTrace(traceid,"tRequestID")= tRequestID } else { set $Namespace = currentNS }
if (sc=$$$OK) { set count=tResponse.PAPersonListExecuteQueryResult.Count() FOR index=1:1:count { set MRN=tResponse.PAPersonListExecuteQueryResult.GetAt(index).RegistrationNo set GivenName=tResponse.PAPersonListExecuteQueryResult.GetAt(index).Name2 set MiddleName=tResponse.PAPersonListExecuteQueryResult.GetAt(index).Name3 set FamilyName=tResponse.PAPersonListExecuteQueryResult.GetAt(index).Name set Gender=tResponse.PAPersonListExecuteQueryResult.GetAt(index).Sex set DateOfBirth=$zdate(tResponse.PAPersonListExecuteQueryResult.GetAt(index).Dob,3)
set ^CacheTemp("TRAK",repid,ind)=$lb(MRN,GivenName,MiddleName,FamilyName,Gender,DateOfBirth) set ind=ind+1
} } }
set qHandle=$listbuild(0,repid,0) QUIT $$$OK

     // This fetch method should never have to change.
	// Restore QHandle
	set AtEnd=$list(qHandle,1)
	set repid=$list(qHandle,2)
	set ind=$list(qHandle,3)
	set ind=$order(^CacheTemp("TRAK",repid,ind))
	if ind="" {	// if there are no more rows, finish fetching
		set AtEnd=1
		set Row=""
	}  else  {	// fetch row
		set Row=^CacheTemp("TRAK",repid,ind)
	}
	// Save QHandle
	set qHandle=$listbuild(AtEnd,repid,ind)
        QUIT $$$OK

// Setup product logo for HealthShare	
Set tProduct = "HealthShare"	
If $$$EnsMajorVersion_$$$EnsMinorVersion < 20172 {
	Set tProductLogo = "portal/" _ tProduct _ "_Logo.png"
}
Else {
	Set tProductLogo = "portal/InterSystems IRIS.png"
	If ##class(%ZHSLIB.HealthShareMgr).InstanceType() '= $$$HSisIRISforHealth {
		Set tProductLogo = "portal/HealthShareLogo210x50.png"
	}
}
Set tServerName = $$$HSNetworkHostName
#if $$$HSDebug
	Set tServerName = tServerName_":"_%session.SessionId
#endif	

If pShowSmallMenu {
	Do ..%OnGetSmallMenu(pSourcePage,pManageEnsemble,.tSmallMenu)
}
Set tSC = ..%DrawTitleHTML(tProduct_"^"_tProductLogo_"^"_tServerName,pTitle,.tSmallMenu,.tLocatorMenu)

Set tLink=%request.URL
&html<<label id="idHSLogout" style="display: none;">#(tLink)#</label>>
Quit $$$OK

Set tVersion = $$$EnsMajorVersion_$$$EnsMinorVersion
// For HealthShare, keep old SMP
Set isHealthShareInstance = ##class(%ZHSLIB.HealthShareMgr).IsHealthShareInstance()
If (tVersion > 20191) && 'isHealthShareInstance {
	Quit ..DrawStandardTitle(pTitle,pSourcePage,pManageEnsemble,pShowInfo,pShowAccount,pShowSmallMenu)
}
