			Set n = $O(tCommands(n))
		}
		&html<</td>>
	}

	Set tSC = ..OnDrawRibbon()
	If $$$ISERR(tSC) Quit

	// sort
	If ($D(tSortOptions)) {
		&html<<td style="width:80px;">&nbsp;</td>>
