// This only gets populated in a HealthShare Component namespace
try {
	Set tSC=$$$OK
	Set tType=##class(%ZHSLIB.HealthShareMgr).GetHealthShareNamespaceType()
	Set:tType]"" tSC = $method($this,"Get"_tType_"Array",.pData)
