		// At this time, we are not going to require that the class extend HS.HC.Util.AbstractUIMenuClass
		If (tMenuClass '= "") {
			Set tSC = $classMethod(tMenuClass,"addMenu",args...)
		}
	}
	Catch ex {
		Set tSC = ex.AsStatus()
	}
	If $$$ISERR(tSC) {
		Quit
	}
}
Quit tSC

